	m.push(a);
	n.push(b);
	p.push(null);
}
function(a,b,c){
	m.push(a);
	n.push(b);
	p.push(c);
}
//clear
function c() {
	m=[];
 	n=[];	
 	p=[];
}
//excute
function e(debug){
	for(i in n) {
		result=result.replace(m[i],n[i]);
	}
	if(debug) {
		java.log(debug);
		java.log(result);
	}
	c();
}

if(/Undefined/.exec(result)){
	result= "<br>　　网站分页后第三页有时有问题不可用，速度慢的时候就会是现在这样了，网站速度快的时候就没事了。<br>　　换个时间刷新试试！！！<br>　　换个时间刷新试试！！！<br>　　换个时间刷新试试！！！<br>" + result;
}
//转换空格
x(/[ \t]+</g,'<');
x(/&nbsp;/g,' ');
//初始化分段准备
x(/[\t ]{2,2}/g,"　");
//初始化图片链接
x(/[\r\n]?[\t ]*<img .+?toimg\/data\/([^\.]+)[^>]+>[\t ]*[\r\n]?/g,"#$1#");


//去掉标签里的属性
x(/(\<|&lt;)([^\>]*?)(\s|&nbsp;)[^\>]*(\>|&gt;)/g,"<$2>");
//去掉超链接
