ruleToc.chapterList:
$..vod_play_url
@js:
json=[]
res=String(result[0])
list=(list=res.split('$$$'))?(String(list[1]).match(/\.(mp4|m3u8)/)?list[1]:list[0]).split('#'):res.split('#')
if(list){for(i in list){
		list1=list[i].split('$')
		json.push(JSON.stringify({
				name:list1[0]?'《'+book.name+'》 '+list1[0]:'',
				url:list1[1]?list1[1]:(list1[0].match(/http/)?list1[0]:'')
			}))
	}}
else{
		list1=res.split('$')
		json.push(JSON.stringify({
				name:list1[0]?book.name+list1[0]:'',
				url:list1[1]?list1[1]:(list1[0].match(/http/)?list1[0]:'')
			}))
	}
json

