ruleToc.updateTime:
//a/@title
<js>
var time = /\d{4}-\d{2}-\d{2} \d{2}:\d{2}/g.exec(String(result))
var count = String(/字数：\d{1,5}/g.exec(String(result))).replace(/字数：/g,"")
if (count === "null")
count = String(/\d{1,5}字/g.exec(String(result))).replace(/字/g,"")

result = time + "  " + count + " 字"
</js>

