ruleBookInfo.init:
@js:
var J = org.jsoup.Jsoup.parse(result);
var o = selector => String(J.select(selector).attr('href'));
var w = selector => String(J.select(selector).text());
var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
var book = {
      name: og('novel:book_name'),
      author: og('novel:author'),
      last: og('novel:latest_chapter_name'),
      intro: og('description'),
      cover: og('image'),
      kind: og('novel:category')+' '+og('novel:status')+' '+og('novel:update_time'),
      url:o('#list a:last-child'),
      word:w('.visible-x:nth-child(2)'),
      toc:og('novel:read_url').replace(/(\d+\/)$/,"read/$1")
};
book;

