@js:
//è°ƒè¯•æ ‡å¿—ä½ åªæ˜¾ç¤ºç¬¬ä¸€å¼ å›¾ç‰‡
let DEBUG = 0;

//æœ¬åœ°å¯†é’¥
local_keys={};

//è¿œç¨‹keyé“¾æ¥
// https://github.com/Xwite/autoCI

let remoteUrls = 
[
//"https://raw.githubusercontent.com/Unicellular-SU/autoCI/main/coco_keys.json",
"https://raw.githubusercontents.com/Unicellular-SU/autoCI/main/coco_keys.json", 
"https://cdn.jsdelivr.net/gh/Unicellular-SU/autoCI@main/coco_keys.json",
];

///////
String.prototype.add=function(e){let t={headers:{Referer:baseUrl.replace(/\,\s*\{.*/,"")}};return t=Object.assign(t,e ||{}),`${this},${JSON.stringify(t)}`},Object.prototype.add=function(){return this.toString().add(arguments[0])};

Array.prototype.toImg=function(f){return this.map((t=>`<img src="${f?f(t):t}"/>`)).join("\n")};

function load(t){for(let o of t){let t=java.ajax(o);if(!/exception/i.test(t))return[t,o]}throw"Error: æ‰€æœ‰é“¾æ¥ä¸‹è½½å¤±è´¥"}
////////

function fetchRemoteKeys() {
    let remoteKeys = load(remoteUrls)[0],
    updated = false;
    try {
        JSON.parse(remoteKeys);
        let local = source.getVariable(),
        remote = remoteKeys;
        if (local != remote) {
            source.setVariable(remote);
            java.toast("è¿œç¨‹å¯†é’¥å·²æ›´æ–°ï¼Œé‡è¯•");
            updated = true
        } else {
            java.toast("è¿œç¨‹å¯†é’¥æœªæ›´æ–°ï¼Œå¯ä½¿ç”¨æœ¬åœ°å¯†é’¥è§£æ")
        }
    } catch(e) {
        java.log(remoteKeys);
        throw"å¯†é’¥æ–‡ä»¶è§£æé”™è¯¯";
    }
    return updated
}

//javaå®ç°aes base64è§£å¯† é€Ÿåº¦å¿«ä¸€äº›
function decrypt(key, word) {
	return java.aesBase64DecodeToString(word, key, "aes/ecb/pkcs7padding","")+""
	}
	function base64Decode(base64) {
		return java.base64Decode(base64)+""
		}


function decode(word, keys, name) {
    if (!keys || !keys[name]) throw "â å¯†é’¥ä¸å¯ä¸ºç©º";
    let decrypted = "null"
    for (let key of keys[name]) {
    	try {
        decrypted=decrypt(key, word);
        } catch {}
        if (decrypted=="null") continue
        return decrypted
    }
    //é‡è¯•
    if (fetchRemoteKeys())  return decode(word, keys, name);
    throw`â ${name}è§£å¯†å¤±è´¥`;
}

function decodeImage(src, keys) {
    let C_DATA = src.match(/C_DATA\s*=\s*\S([\s\S]+?)['"]/);
    if (!C_DATA) throw "å›¾ç‰‡æ•°æ®C_DATAè·å–å¤±è´¥ï¼ï¼";
    C_DATA=decode(base64Decode(C_DATA[1]), keys, "C_DATA");
    DEBUG && java.log(C_DATA);
    eval(C_DATA);
    let pics_list = [];
    if (!image_info.img_type) {

        //å›¾ç‰‡è·¯å¾„ å’Œæ€»æ•°
        let path, totalImageCount;
        if (typeof mh_info.enc_code2 == "undefined") {
            path = mh_info.imgpath;
        } else {
            path = decode(base64Decode(mh_info.enc_code2), keys, "enc_code2");
        }
        if (typeof mh_info.enc_code1 == "undefined") {
            totalImageCount = mh_info.totalimg;
        } else {
            totalImageCount = decode(base64Decode(mh_info.enc_code1), keys, "enc_code1");
        }
        //æœåŠ¡å™¨é€‰æ‹© å’Œè·¯çº¿
        let servers = [mh_info.domain], server;
        try {
            eval(src.match(/(var __JS_READLINE[\s\S]+?)<\/script>/)[1]);
            servers.push(__JS_READLINE.domain || __JS_READLINE.data[0].domain);
        } catch (e) {}
        server = mh_info.use_server == "" ? servers[0] : servers[0].replace(/^img/, `img${mh_info.use_server}`);
        //è¯·æ±‚å¤´å’Œå›¾ç‰‡äºŒæ¬¡åŠ å¯†
        let options ={}, suffix="jpg"
        if (image_info.imgKey != undefined && image_info.imgKey != '') {
//ä¸´æ—¶options
 	options= {
     		"headers":{
  "Cookie": `_tka_${mh_info.pageid}=${path};_tkb_${mh_info.pageid}=${totalImageCount}`,
   "Referer":baseUrl,
"Origin":"https://www.cocomanga.com",}
};
enc_webp_bytes_key = decode(image_info.imgKey, keys, "C_DATA");
book.putVariable("enc_webp_key", enc_webp_bytes_key);
suffix="enc.webp";
     }
        for (let i = parseInt(mh_info.startimg); i <= totalImageCount; i++) {
     let url =`https://${server}/comic/${encodeURI(path)}${String(i).padStart(4,"0")}.`;


    pics_list.push(
	(url+suffix).add(options)
	)


}
    } else {
    	  //ç›´é“¾ç±»å‹
        let urls__direct=base64Decode(image_info.urls__direct).split("|SEPARATER|");
        if(image_info.img_type==1) {
         pics_list = urls__direct.map(url=>url+".webp");
        } else {
            pics_list = urls__direct
        }
    }  
return DEBUG ? pics_list.slice(0,1).toImg()
: pics_list.toImg()
}


try {
  	  java.log('â‡ï¸ ä½¿ç”¨æœ¬åœ°å¯†é’¥');
    decodeImage(src, local_keys);
} catch(e) {
	   java.log(e);
    java.log('âœ³ï¸ ä½¿ç”¨è¿œç¨‹å¯†é’¥');
    source.getVariable() == null && fetchRemoteKeys();
    decodeImage(src, JSON.parse(source.getVariable()));
}

