@js:
function encrypt(str){
  var word = "283hgW923*$@~~9023";
  var replace = java.base64Encode(str).replace("\n", "");
  var charArray1 = replace.split("");
  var charArray2 = java.md5Encode(word).split("");
  var i = 0;
  for (var i2 = 0; i2 < charArray1.length; i2++) {
    if(charArray1[i] != ""){
      if (i > charArray2.length - 1) {
        i = 0;
      }
      var c = charArray1[i2];
      var c2 = 5;
      if (charArray2[i].charCodeAt() <= 5) {
        c2 = charArray2[i];
      }
      var i3 = Number(c.charCodeAt()) + Number(c2);
      if (i3 > 65535) {
        i3 -= 65535;
      }
      charArray1[i2] = String.fromCharCode(i3);
    }
    i++;
  }
  //java.log(charArray1.join(""))
  return java.base64Encode(charArray1.join("")).replace("+", "-").replace("/", "_").replace("=", "~").replace("\n", "");
}

function sign(list){
  var sb='';
  list=list.sort();
  for(var i=0;i<list.length;i++){sb+=list[i]+"&";}
  sb+="key=NV~!F8dgweHS^%$GKS39092337820*23*%(8";
  return encrypt(java.md5Encode(sb));
}

