url=java.get("uri")
uri=java.get("url")

list=[]
for(i=0;i<a.length;i++){
b=String(a[i])
if(b.indexOf("html")==-1){
	c=b.match(/\d+/g)
		list[i]=uri+"/"+c[0]+"/"+c[1]+"/"+c[2]+"_"+c[3]+'.html,{"webView":true}'
	}else{
list[i]=url+b
}
}
list

