$function getImgList() { 
var str = html.match(/end_var:(\d+),.*middle:\"(.*?)\$\$(.*?)\"/);
var pn = parseInt(str[1]); 
var piclist = new Array([pn]); 
for (var i = 0; i < pn; i++) { 
piclist[i] = "http://mhpic.zymkcdn.com/comic/"+str[2]+(i+1).toString()+str[3]; 
} 
return piclist; 
}
