ruleChapterList:
+<js>
//二级目录，Json
var jdoc = JSON.parse(result);
var chUrl = 'https://nnv3api.dmzj1.com/novel/download/@get:{ID}_';
var volUrl = 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text=';
var list = [];
//显示卷名
var opts = {
  showVolName: false
};

sm=java.get('sm');
author=java.get('author');

function format(title){
  return title.trim().replace(/^(\d+)(?![\s\d卷部章节回.])/, '$1 ').replace(/\s+/g, "\x20\x20");
}

jdoc.forEach((vol,idx) => {
  var chs = vol.chapters.map(ch => ({
    text:(opts.showVolName ? '🍥[' + format(vol.volume_name) + ']🍥' : (ch.IsVip === '1' ? '💰' : '').padStart(3, "\u2000")) +format(ch.chapter_name),
    href: chUrl+vol.volume_id+'_'+ch.chapter_id+'.txt',
    name: format(ch.chapter_name)
  }));

  var volInfo =author+"：\n"+sm+'\\'+format(vol.volume_name) + '\n目录(' + chs.length + ")：\n";
  volInfo += chs.slice(0,30).map(v => v.name).join("\n");
  volInfo += chs.length > 30 ? "\n……" : '';
  list.push({
    text: '♦[' + format(vol.volume_name) + ']♦',
    href: volUrl + encodeURIComponent(volInfo)
  });
  list = list.concat(chs);
});

result = list;
</js>

ruleSearchList:
$.[*]
<js>
var opts = {
  pattern: /\/show\/1\/([^\/]+)\//,
  name: '$.title',
  author: '$.authors'
};

//删除糢糊匹配的搜索结果，然后按书名长度排序。依赖于baseUrl、result变量。
//return Array
//opts Object: 参数，包含以下key：
//target List<Object>(result): 要过滤列表。
//pattern RegExp: 匹配searchKey的正则表达式。
//name String: 书名规则。
//author String: 作者规则。
//charset String('UTF-8'): 编码方式。
//sorting Boolean(true): 是否重新排序。

function searchFilter(opts){
  result = opts.target === undefined ? result : opts.target;
  opts.charset = opts.charset === undefined ? 'UTF-8' : opts.charset;
  opts.sorting = opts.sorting === undefined ? true : !!opts.sorting;
  function getText(ele, rule){
    java.setContent(ele);
    return String(java.getString(rule));
  }
  try {
    var kw = (baseUrl.match(opts.pattern) || ['',''])[1];
    kw = Packages.java.net.URLDecoder.decode(kw, opts.charset);
    result = result.toArray().filter(e => getText(e, opts.name).includes(kw) || getText(e, opts.author).includes(kw));
    return opts.sorting ? result.sort((a, b) => getText(a, opts.name).length - getText(b, opts.name).length) : result;
  } catch(e) {
    return result;
  }
}

searchFilter(opts);
</js>

