	"body":"act={{
h = java.ajax(source.getKey()),
    j = org.jsoup.Jsoup.parse(h),
    f = j.select('#searchForm'),
    v = f.select('[name=act]').attr('value');

function ckStore(ck) {
    this._value = ck;
    this.keyPairs = {};
    this.parse = () => {
        this._value.split(';').forEach(v => {
            kv = v.split('=').map(v => v.trim());
            this.keyPairs[kv[0]] = kv[1];
        });
        return this;
    }
    this.set = (k, v) => {
        this.keyPairs[k] = v;
        return this;
    };
    this.value = () => {
        res = void(0);
        for (k in this.keyPairs) {
            kv = `${k}=${this.keyPairs[k]};`;
            res = res === void(0) ?
                kv :
                res + kv;
        }
        return res;
    };
}
ckval = cookie.getCookie(source.getKey());
sqval = java.encodeURI(key, 'gbk');
st = new ckStore(ckval).parse().set('ssqq', sqval);
cookie.setCookie(source.getKey(), st.value());
java.put('key', key);
java.put('act', v);
	}}&q={{key}}&submit=搜索 "
