@js:
var eval_text = result.match(/eval\(.*\)/);
if (eval_text) {
    eval(eval_text[0]);
    var r = org.jsoup.Jsoup.parse(result);
    var tags = r.select('.content p');
    var arr = [];

    if (tcl == 1) {
        for (var i = 0; i < imgpcl.length; i++) {
            tags[imgicl[i] - 1] = `<img src="https://www.clewxx.com${imgpcl[i]}?r=${Math.random()}">`
        }
    }
    for (var i in tags) {
        if (i < icl) {
            arr.push(tags[i])
        }
    }

    var con_arr = [],
        cp = r.select('.content p');
    for (var i = 0; i < pcl.length; i++) {
        con_arr[pcl[i]] = cp[icl + i];
    }
    var result_arr = arr.concat(con_arr)
    result_arr.join('')
}else{
	  org.jsoup.Jsoup.parse(result).select('.content p');
}

