  if(vol.name!=""){
  var volInfo = sm+'\\'+ format(vol.name);
  list.push({
    text: '————◆ [' + format(vol.name) + '] ◆————',
    href: volUrl + encodeURIComponent(volInfo),

    VolName:true
    });
    }
  list = list.concat(chs);
});
result = list;
</js>

