ruleExplore.bookList:
@js:
  let list = [];
  let resultData = JSON.parse(src).data;
  let data;
  if(resultData.book_shelf_info){
    data = resultData.book_shelf_info;
    let requestData = {books: []};
    for(let i in data){
      requestData["books"].push({
        book_id: data[i]["book_id"],
        item_id: "0"
      });
    }
    let booksData = java.ajax("https://fanqienovel.com/api/bookshelf/multidetail,{method: \"post\",body: " + JSON.stringify(requestData) + "}");
    let books = JSON.parse(booksData).data.detail_list;
    for(let i in books){
      let d = books[i];
      list.push({
        src: d,
        book_name: d.book_name,
        thumb_url: d.thumb_url,
        abstract: d.abstract,
        url: `https://novel.snssdk.com/api/novel/book/directory/list/v1?book_id=${d.book_id}`
      });
    }
  } else if(resultData.data.book_info){
    data = resultData.data.book_info;
    for(let i in data){
      let d = data[i];
      book = JSON.parse(java.ajax("https://novel.snssdk.com/api/novel/book/directory/list/v1?book_id="+d.book_id)).data
      lastChapterTitle = book.book_info.last_chapter_title + "·"  + java.timeFormat(d.last_publish_time * 1000)
      list.push({
        src: d,
        author: d.author,
        book_name: d.book_name,
        thumb_url: d.thumb_url,
        abstract: d.abstract,
        word_number: d.word_number,
        lastChapterTitle: lastChapterTitle ,
        tags: d.tags,
        url: `https://novel.snssdk.com/api/novel/book/directory/list/v1?book_id=${d.book_id}`
      });
    }
  }
  list

