coverDecodeJs:
var Magua = new JavaImporter();
Magua.importPackage(
    Packages.java.io,
    Packages.android.graphics
);
with(Magua){
let get_corp_count = (id, page)=>{
    if (id.includes("cover")) {
    	  return 30;
    };
    let count = 10;
    let n = java.md5Encode(id + page);
    switch (n=n.substr(-1).charCodeAt(),n %= 10) {
        case 0:count = 44;break;
        case 1:count = 48;break;
        case 2:count = 52;break;
        case 3:count = 56;break;
        case 4:count = 60;break;
        case 5:count = 64;break;
        case 6:count = 68;break;
        case 7:count = 72;break;
        case 8:count = 76;break;
        case 9:count = 80
    }
    return count
}

((result,src)=>{
    var img = BitmapFactory.decodeByteArray(result,0,result.length);
    var height = img.getHeight();
    var width = img.getWidth();

    var newImg = Bitmap.createBitmap(width,height,Bitmap.Config.ARGB_8888);
    var canvas = new Canvas(newImg);

    var id = src.match(/(\d+|cover_mobile).jpg/)[1];
    let count = get_corp_count(id, 0);

    /*å›¾ç‰‡é‡ç»„æ ¸å¿ƒ*/
    for (var mod = parseInt(height % count),block_index = 0; block_index < count; block_index++) {
        var corp_height = Math.floor(height / count);
        var p = corp_height * block_index;
        var u = height - corp_height * (block_index + 1) - mod;

        0==block_index?corp_height += mod:p += mod,canvas.drawBitmap(Bitmap.createBitmap(img,0,u,width,corp_height),0,p,null);
    }

    var newBit = new ByteArrayOutputStream();
    newImg.compress(Bitmap.CompressFormat.PNG,100,newBit);
    return newBit.toByteArray();
})(result,src)
}

