//启用/禁用
function $(sort, name) {
    get();
    if (name == undefined) name = sort.split(',')[0];
    name = name.trim()
    if (name == '全部分类') {
        sort = sortName;
    } else { //屏蔽错误分类
        sort1 = []
        sort2 = sortName.split(',')
        sort.split(',').map(key1 => {
            key1 = key1.trim();
            for (i in sort2) {
                key2 = sort2[i].trim();
                if (key1 == key2) {
                    sort1.push(key1);
                    break;
                }
            }
        })
        sort = sort1.join(',')
    }
    if (!/^\s*$/.test(sort)) {
        sort = String(sort).split(',');
        f = sort.length > 1 ? '分组' : '分类';
        V = v[sort[0]];
        if (name == '全部分类') v = {};
        login('【更改成功】\n『' + f + '』 ' + name + ': 已' + (V ? '禁用' : '启用'));
        sort.map(key => v[key] = V ? 0 : 1);
        return set(v);
    }
    return login('【无法更改】\n原因：没有有效分类(分类名称不正确)')
}

