function seriesHandler(res) {
    const limit = 30
    let returnList = [];
    let seriesID = res.seriesNavData.seriesId
    let allChaptersCount = (() => {
        let result = util.cacheGetAndSet(util.urlSeries(seriesID), () => {
            return JSON.parse(java.ajax(util.urlSeries(seriesID)))
        }).body.total
        util.debugFunc(() => {
