bookSourceComment:
/*
	下载：http://www.kuaidu.co
http://langyaxiaoshuo.com/cruisereader/api/home/search,{"body":{"keyword":"{{key}}"},"method":"POST"}
*/

// 发现显示最新章节？(0禁用，1启用)
isC = 0
// 章节目录启用分卷？(0禁用，1启用)
isVol = 0
// 接口选择 ( 0 / 1 )
api_type = 1
搜索接口 = api_type ? "ssxs.pigqq" : "souxs.leeyegy";
正文接口 = api_type ? "wb.pigqq" : "quapp.shenbabao";
封面接口 = api_type ? "imgapixs.pigqq" : "imgapixs.pysmei";
// uri解密
duri = () => {
    uri = java.hexDecodeToString(result);
    if (/search.aspx/.test(uri))
        url = 搜索接口;
    else
        url = 正文接口;
    return `https://${url}.com` + uri;
}
// img处理
gImg = ($Img, $Id) => {
    $Img = String($Img);
    if ($Img.match('nopic.gif'))
        $Img = $Id + '.jpg';
    else
        $Img = $Img.replace(/^.*BookImages\//i, '');
    if (!/^http/.test($Img))
        return `https://${封面接口}.com/BookFiles/BookImages/` + $Img;
    else
        return $Img;
}
// 请求 & 解密
cipher = java.createSymmetricCrypto("DESede/CBC/PKCS5Padding", "OW84U8Eerdb99rtsTXWSILDO", "SK8bncVu");
run = () => {
    result = String(java.ajax(duri()))
        // des解密
        .replace(/[^"]*[{}]{6,}([^"]+)/g, ($0, $1) =>
            String(cipher.decryptStr($1))
            // 防止JSON.parse报错
            .replace(/\n/g, '\\n')
            .replace(/\r/g, '\\r')
            .replace(/\f/g, '\\f')
            .replace(/\t/g, '\\t')
            .replace(/\"/g, '\\"')
        ).replace(/},(}|])/g, '}$1');
    return $ = JSON.parse(result).data;
}
// 替换转义字符
tReg = (text) => String(text)
    .replace(/\\n|(<|\s+)br>/g, '\n')
    .replace(/\\r/g, '\r')
    .replace(/\\f/g, '\f')
    .replace(/\\t/g, '\t')
    .replace(/&(lt|gt|nbsp|amp|quot);/gi, ($0, $1) => ({
        'lt': '<',
        'gt': '>',
        'nbsp': ' ',
        'amp': '&',
        'quot': '"'
    }[$1]));

exploreUrl:
@js:
sort=[];
push=(title,url,type)=>sort.push({
		title: title,
		url: url,
		style: {
				layout_flexGrow: 1,
				layout_flexBasisPercent: type
			}
	});


// 通用数组
arr = [["最热","hot"],["新书","new"],["评分","vote"],["完结","over"]];


// data:url
$$=(a,b,c)=>{
	if(c) uri = `/top/${a}/top/${b}/${c}`;
	else uri = `/Categories/${a}/${b}`;
	return `data:;base64,{{java.base64Encode("${uri}/"+page+".html")}\},{"type":"biqugeapp"}`;
}


// 排行
[["周","week"],["月","month"],["总","total"]]
.map([A,c]=>{
		push(`༺ˇ»\`ʚ${A}排行榜ɞ´«ˇ༻`,"",1);
		arr.concat([["推荐","commend"],["收藏","collect"]])
		.map([C,b]=>{
				[["男","man"],["女","lady"]].map([B,a]=>{
						title = `${B}频${C}榜`;
						push(title,$$(a,b,c),0.4);
					})
			})
	});


// 分类
arr.map([title,b]=>{
		push(`༺ˇ»\`ʚ${title}专区ɞ´«ˇ༻`,null,1);
		["玄幻奇幻", "武侠仙侠", "都市言情", "历史军事", "科幻灵异", "网游竞技", "女生频道", "同人小说"]
		.map((title,a)=>{
				a++; if(a==8) a=66;
				push(title,$$(a,b),0.25);
			});
	});


// 格式化
JSON.stringify(sort);

