ruleBookInfo.init:
@js:
eval(String(source.bookSourceComment));
run();
$.kind = [$.CName, $.BookStatus,$.LastTime].join(',');
$.Score = $.BookVote.Score+'分';
$.LastChapter += '•'+$.LastTime.replace(/ .*/g,'');
$.Img = gImg($.Img,$.Id);
$.Desc = tReg($.Desc);

uri = `/BookFiles/Html/${Math.ceil($.Id/1e3)}/${$.Id}/index.html`;
uri = java.base64Encode(uri);
$.tocUrl = `data:;base64,${uri},{"type":"biqugeapp"}`;
$

ruleSearch.bookList:
@js:
eval(String(source.bookSourceComment));
run();


isB = $.hasOwnProperty('BookList');
($.BookList||$).map($=>{


// 书籍链接
uri = `/BookFiles/Html/${Math.ceil($.Id/1e3)}/${$.Id}/info.html`;
if(isB&&isC){// 发现显示最新章节
	result = java.hexEncodeToString(uri);
	$ = run();
}
$.bookUrl = `data:;base64,${java.base64Encode(uri)},{"type":"biqugeapp"}`;


// 参数整理
	$.kind = [$.CName, $.BookStatus,$.LastTime].join(',');
	$.Score = ($.BookVote?$.BookVote.Score:$.Score)+'分';
	$.LastChapter = ($.LastChapter||"")+' • '+($.LastTime||"");
	$.Img = gImg($.Img,$.Id);
	$.Desc = tReg($.Desc);
	return $;
});

ruleToc.chapterList:
@js:
eval(String(source.bookSourceComment));
run();

u = `/BookFiles/Html/${Math.ceil($.id/1e3)}/${$.id}/`;


list = [];
push = ($, isVol) => {
    json = {
        name: tReg($.name),
        volume: isVol
    }
    if (!isVol){
    	uri = u + $.id + '.html';
    	uri = java.base64Encode(uri);
    	json.url = `data:;base64,${uri},{"type":"biqugeapp"}`;
    	}
    list.push(json);
}


$.list.map(($, i) => {
    volNum = i;
    if (isVol) push($, true);
    $.list.map($ => {
        if ($.hasContent == 1) push($, false);
    });
});


// 卷数小于2时，不显示分卷
if (isVol && !volNum) list.splice(0, 1);

list

