ruleToc.chapterList:
@js:
//章节开始
var star=java.getString("text.阅读@href")
var start=star.match(/(\d+).html/)[1];
//章节末尾|最新章节
var end=java.getString("text.最新章节@a@href");
var ends=end.match(/(\d+).html/)[1];
//初始化章节列表
var list=[{text:"第一章",href:star}];
//判断目录数量
if(ends-start<10000){
	for(i=1;start<=ends;i++,start++){
		name=`第${i}章`
		link=String(star).replace(/\d+.html/,`${start}.html`)
		list.push({text:name,href:link})
		}
		list;
	}else{
		java.toast("目录异常，章节列表获取失败！")
		list=[{text:"正文",href:star}];
		}
list;

