@js:
eval(String(source.loginUrl));
var list=".book-list-1";
var data="$.data[*]";
var item=[];
var su=url;
var sort=/sort/.test(baseUrl)
if(v==1||sort){
	book(list,item)
	}else{apis(data,item)}

function book(path,item){
	var li=java.getElements(path)
	for(i in li){
		name=java.getString("h4@text",li[i])
		author=java.getString("p.0@text",li[i]);
kind=java.getString("span.-3:-1@text",li[i]);
word=java.getString("span.-2@text",li[i])
		intro=java.getString("p.1@text",li[i]);
img=java.getString("img@data-src",li[i])
		url=java.getString("a.1@href",li[i]);
obj={name:name,author:author,kind:kind,word:word,intro:intro,img:img,url:url};
   item.push(obj);
		}
		return item;
	}

function apis(path,item){
	var a=java.getElements(path)
	for(i in a){
name=java.getString("$.articlename",a[i]);
last=java.getString("$.chaptername",a[i])
		url=java.getString("$.url",a[i])
		url=su+url;
		obj={name:name,last:last,url:url};
		item.push(obj)
		}
		return item;
	}

item

