loginUrl:
/*——域名接口设置——*/

//域名接口
BurlList=[["原域名","http://api.kuduwxw.com"],["新域名","http://qyapi.kuduwxw.com"]];

var v=source.getVariable();
	if(v==null||v==""){
		burl = BurlList[0],Nam=burl[0],burl=burl[1];
		java.toast("\n❗未设置❗\n默认启用原域名："+Nam+"/"+burl);
		//默认启用原域名
		source.setVariable("0");
	}
else{
	var v = v.match(/\d+$/)[0];
 burl = BurlList[v],Nam=burl[0],burl=burl[1];
}

function get(n){	  
	burl=BurlList[n];
	java.toast('【域名接口信息】\n\n\n　　当前接口：'+burl[0]+burl[1]);
}	

function ym(i){
	burl=BurlList[i];
	java.toast('【设置成功提示】\n\n\n　　已选择【'+burl[0]+burl[1]+'】为当前接口');
	return source.setVariable(i)
	}




/*——分类接口设置——*/	

//分类接口
FlagList=[["热门","hot"],["评分","score"],["新书","new"],["精选","is_greatest"]];

//获取登入头
	Head = source.getLoginHeaderMap();

//初始化
Flag = JSON.parse(JSON.stringify(Head));
if(Flag==null) Flag={};

if(Head){
		ID = Head.sid;
	  Name=FlagList[ID][0];
		flag = Head.flag;
	}
else{
	//默认设置，【热门分类】接口	
		ID = "0";
	  Name = FlagList[ID][0];
	  flag = FlagList[ID][1];
		java.toast('\n❗未设置❗\n默认启用		 【'+Name+'分类】 接口：'+flag);

	 Flag.sid=ID;
		Flag.flag = flag;
		put(Flag);

	}


//设置注意事项
function log(t){
		java.longToast('【设置注意事项】\n\n\n  ㈠ 请点击下方按键进行相应设置\n  ㈡ 设置后请刷新\n  ㈢ 若原域名正文内容提示app更新，请更换为新域名\n')
	}

//放置登入头
function put(json){
		return source.putLoginHeader(
			JSON.stringify(json)
		);
	}

//设置名称
function name(i){
		if(i==undefined) i=0;//默认设置
		return ' 【'+FlagList[i][0]+'分类】 '
	}

//当前设置
function look(i){
	if(Flag.sid)
		java.toast('【分类接口信息】\n\n\n　　当前接口'+name(Flag.sid));
  else
		 java.toast('【分类接口信息】\n\n\n　　❗未设置❗默认启用'+name(0));
	}	 

//设置
function set(i){ 
   Flag.sid=i
		Flag.flag = FlagList[i][1];

		java.toast('【设置成功提示】\n\n\n　　已选择'+name(i)+'为当前接口');
		return put(Flag);			
}

