class.spotlight-group@tag.div@data-src##\n##,
<js>result</js>

<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0">
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>

<div id="box"></div>
<h6 id="loads"><i>加载中...</i></h6>



<script>
var srclist = "{{result}}" .split(",");
var page = srclist.length;

var box = document.getElementById("box");
var loads = document.getElementById("loads");

var h5='<h5 style="border-left:2px solid#000;margin-bottom:0;padding-left:5;">'

//初始加载四张图
for(var i=0; i<3; i++){
	box.innerHTML += h5+(i+1)+"/"+page+"</h5>"
	box.innerHTML += '<img onload="box.viewer.update()" src="' + srclist[i] + '">';
}

document.querySelector("img").onload = () => {
	new Viewer(box,{title:false,interval:3000});
	loads.id = "load";
//从第五张图开始
	var i = 3;

	io = new IntersectionObserver(x=>{
//相交等于0时终止运行？
		if(x[0].intersectionRatio<=0) return;

		for(var I=0; I<3; I++){
				getImg(i,page,srclist)
				i+=1
		}
	});
	io.observe(load);
};

function getImg(i,page,srclist){
	if( i<=page  &&  srclist[i]!=undefined ){
		box.innerHTML += h5+(i+1)+"/"+page+"</h5>";
		box.innerHTML += '<img onload="box.viewer.update()"  src="' + srclist[i] +'">';
	}else{
		io.unobserve(load);
		load.innerHTML = ""
	}
}
</script>

