ruleChapterList:
@js:
var doc = org.jsoup.Jsoup.parse(result);
var update1 = doc.select(".chalist1 .milestone").text().replace("更新至","").trim();
var update2 = doc.select(".chalist2 .milestone").text().replace("更新至","").trim();
if (update1) {
  var doc1 = doc.select(".chalist1 .chapter-list");
  var test1 = String(doc1).includes(update1);
}
if (update2) {
  var doc2 = doc.select(".chalist2 .chapter-list");
  var test2 = String(doc2).includes(update2);
}
var chap = (test1&&doc1) || (test2&&doc2);
var list1 = new Array();
list1 = chap&&chap.select("a").toArray().map(a=>{
  return {chapterid:String(a).match(/\/(\d+)\.html/)[1],chaptername:a.text()};
});
var list2 = new Array();
try {
  var bid = doc.select("dd").attr("data-id");
  var vid = doc.select("dd").attr("data-vid");
  list2 = JSON.parse(org.jsoup.Jsoup.connect("http://m.sixmanhua.com/bookchapter/").requestBody("id="+bid+"&id2="+vid).post().text());
} catch(e) {}
list1.concat(list2).reverse()

