//获取网址和变量
var u=source.key,
v=source.getVariable();
//如果获取失败用webview动态加载获取
function web(){
	var w=java.webView(null,u,null);
	var s=w.match(/el-tag.*window.open.'(.+?)','/)[1]
	h=/^https?/.test(s);
	//网址如果不完整，二次加载
	if(h){
		s=s;
		}else{
			we=java.webView(null,u+s,null);
			s=we.match(/el-tag.*window.open.'(.+?)','/)[1];
			}
	return s;
	}
//获取搜索地址
var url;
if(!v||v==""){
	var x=java.ajax(u),
	p=org.jsoup.Jsoup.parse(x),
j=p.select("head>script").get(1).attr('src');
	var html=java.ajax(u+j),
	a=html.match(/action="(.+?)"/);
	var so=a?a[1]:web();
	source.setVariable(so);
	url=so;
	}else{url=v}
url;

