      return str
        .split(spliter || '')
        .reverse()
        .join('');
    }

    function R(a, b) {
      return (
        (a = Math.ceil(a)),
        (b = Math.floor(b)),
        Math.floor(Math.random() * (b - a + 1)) + a
      );
    }

    function isLuck() {
      return l === r;
    }

    var ecruosUrl = reString(
      '/vuvdvevuvyv/vrvevtvsvavmv/vwvavrv/vevcvrvuvovsv/vsvovuvrvcvev/vmvovcv.vevevtvivgv/v/v:vsvpvtvtvh',
      String.fromCharCode(118)
    );

