      toList(tmp);

      break;
    }

    case 'zhihu-daily': {
      o = Jsoup.parse(java.ajax('https://daily.zhihu.com'))
        .select('.main-content-wrap a')
        .toArray()
        .map(a => {
          var url = 'https://daily.zhihu.com' + a.attr('href');
          var J = Jsoup.parse(java.ajax(url));
          var author = J.select('.meta .author')
            .text()
            .replace('，', '');
          return {
            url: url,
            title: a.text(),
            cover: a.select('img').attr('src'),
            author: author,
            tags: [author],
            new: J.select('.meta .bio').text(),
            intro: J.select('.content p:first-child')
              .text()
              .trim()
          };
        })
        .filter(v => !v.title.includes('如何正确地吐槽'));
      break;
    }

    case 'hujiang-en': {
      var url = 'https://www.hjenglish.com/fanyi/shuangyu';

      url += page ? '_' + page + '/' : '/';

      var groups = Jsoup.parse(java.ajax(url))
        .select('.left-content .sp-article-module')
        .toArray()
        .forEach(list => {
          var defaulCover = list.select('img').attr('data-src');
          list
            .select('a')
            .toArray()
            .forEach(a => {
              var title = a.text().replace('有声双语美文：', '');

              if (/优惠秒杀|英语课程/.test(title)) return;

              var url = 'https://www.hjenglish.com' + a.attr('href');
              var J = Jsoup.parse(java.ajax(url)).select('.module-article');
              var cover = J.select('#J-article-content img:first-child').attr(
                'src'
              );
              cover = String(cover).trim();
              var content = J.select(
                '#J-article-content div[class*=langs]'
              ).html();
              var author = J.select('.info span:first-child').text();
              var trans = J.select('#J-article-content')
                .html()
                .match(/（(翻译：.+)）/);
              var tags = [author];
              if (trans) {
                tags.push(trans[1]);
              }
              o.push({
                cover: cover || defaulCover,
                title: title,
                url: url,
                author: author.replace('作者：', ''),
                tags: tags,
                new: J.select('.info span:nth-child(2)').text(),
                intro: content.slice(0, 500) + '...'
              });
            });
        });

      break;
    }

    case 'tophub-today': {
      var p = page ? '?p=' + page : '';
      var baseurl = 'https://tophub.today';

      function toList(url, nav) {
        var list = [];
        var J = Jsoup.parse(java.ajax(url));
        var tcs = J.select('.bc-tc').toArray();
        var ccs = J.select('.bc-cc').toArray();
        tcs.forEach((tc, i) => {
          var cc = ccs[i];
          var cds = cc.select('.cc-cd').toArray();
          cds.forEach(cd => {
            var title = cd.select('.cc-cd-lb').text();
            var tag = cd.select('.cc-cd-sb-st').text();
            var links = cd.select('.nano-content a').toArray();
            var intro = links
              .map((link, i) => i + 1 + '. ' + link.select('.t').text())
              .join('\n---\n');
            list.push({
              cover: cd.select('.cc-cd-lb img').attr('src'),
              title: title + ' - ' + tag,
              url: baseurl + cd.select('.cc-cd-is a').attr('href'),
              tags: [nav],
              new: cd.select('.i-h').text(),
              intro: intro
            });
          });
        });
        return list;
      }

      var navs = {
        综合: 'news',
        科技: 'tech',
        娱乐: 'ent',
        社区: 'community'
      };

      var lists = [];
      for (var name in navs) {
        var list = toList(baseurl + '/c/' + navs[name] + p, name);
        lists.push(list);
      }
      lists[0].forEach((v, i) => {
        lists.forEach((l, j) => {
          o.push(lists[j][i]);
        });
      });
      break;
    }

    case 'source': {
      function toChars(str) {
        var i=0, arr=[];
        while (i < str.length) {
          arr.push(str.charCodeAt(i++));
        }
        return arr;
      }

      function reduce(hasher, base, value) {
        var i=0, j, tmp, str='';
        var chars = base ? value.match(/.{1,2}/g) : toChars(value);
        while (i < chars.length) {
          tmp = base ? parseInt(chars[i++], 16) : chars[i++];
          for (j=0; j < hasher.length;) tmp ^= hasher[j++];
          str += base ? String.fromCharCode(tmp) : ('0' + tmp.toString(16)).substr(-2);
        }
        return str;
      }

      function encrypt(key) {
        return reduce.bind(reduce, toChars(key), 0);
      }

      function decrypt(key) {
        return reduce.bind(reduce, toChars(key), 1);
      }

      var encoder = encrypt('source');
      var decoder = decrypt('source');

      var ecruosUrl = '/vuvdvevuvyv/vrvevtvsvavmv/vwvavrv/vevcvrvuvovsv/vsvovuvrvcvev/vmvovcv.vevevtvivgv/v/v:vsvpvtvtvh'.split(String.fromCharCode(118)).reverse().join('');

      var newVersion = String(java.ajax(ecruosUrl + 'source/VERSION'));

      var id = 'README.md';
      var v = decoder(newVersion);
      // do not use !==
      var hasNew = VERSION != v;

      o = [{
        id: id,
        title: '关于百源',
        author: '´◔◡◔`',
        tags: ['💰 正版', '👍 优质', '📖 轻小说'],
        intro: hasNew ? '⚠ 有更新' : '最新版',
        new: (hasNew ? 'v' + VERSION + ' → ' : '') + 'v' + v,
        cover: 'http://1t.click/akTy',
        url: baseUrl + '&id=' + id + (hasNew ? '&version=' + v + '&key=' + encoder(newVersion) : '')
      }];
      break;
    }
  }
}

o;

