@js:
list = [];
d = li => href = li.select("a").attr("href");

result = result.toArray();
result.forEach((li, i) => {
    if (/volumes /.test(li))
        list.push({
            text: li.text(),
            volume: true
        });
    else {
        d(li);
        if (/JavaScript/i.test(href)) {
            if (result[i + 1] && !/volumes|JavaScript/i.test(result[i + 1])) {
                d(result[i + 1]);
                href += "?★";
            } else {
                d(result[i - 1]);
                href += "?☆";
            }
            // java.log(li.text()+': '+href);
        }

        list.push({
            text: li.text(),
            href: href
        });
    }
});
list

