r.select("script,noscript,style,head>:not(meta),footer,[class~=^foot],[id~=^foot],a:has(>:last-child:matchesOwn(^分类$)),[value]").remove();

n=String(book.name);
m=String(r).replace(/(?:&nbsp;)+/g," ");
书=java.get("源");

if(x=(y=r.select('[property$=book_name]')).size()){
y=y.attr("content");
u=书.indexOf(y)
}else for(u=true,c=r.select("h1,h2,h3,strong").eachText();x<c.size();x++)if(
(y=c.get(x))!=''&&(u=书.indexOf(y),~u&&u<4))break;
if(!~(y=String(y)).indexOf(n)&&~u)n=y;

if((x=r.select("[property$=author]")
).size()){
