@js:// 章节目录规则
// 章节开始
var star = baseUrl+"&page=1";
var start = 1;
start = Number(start);
// 章节末尾|最新章节
var end = java.getString("#last_link@href");
var ends = end.match(/page=(\d+)$/);
if (ends) {
ends = ends[1];
} else {
java.toast("此书籍无法在线阅读");
}
ends = Number(ends);
// 初始化章节列表
var list = [];
// 判断目录数量
if (ends - start < 15000) {
    for (i = 1; start <= ends; i++, start++) {
        name = `第${i}章`;
        link = String(star).replace(/&page=\d+$/, `&page=${start}`);
        list.push({ text: name, href: link });
    }
    list;
} else {
    java.toast("目录异常，章节列表获取失败！");
    list = [{ text: "正文", href: star }];
}
list;

