ruleSearch.author:
tag.a.-1@href@js:
result=java.ajax('https:'+result);
var author=result.match(/作者是([^，]*)/);
var tag=result.match(/类型是([^，]*)/);
var cover=result.match(/src="([^"]*?jpg)/);
var intro=result.match(/<br>([^<]*)/);
java.put('tag',(tag) ? tag[1]:'');
java.put('cover',(cover) ? cover[1]:'');
java.put('intro',(intro) ? intro[1]:'');
(author) ? author[1]:'';

