var reg=new RegExp(/<dd(.).+?"(\d+\.html)">(.*?)<\/a>/g);
var list1=[];
var list2=[];
var tmp,url,name;
var i=-1;
while((tmp=reg.exec(result))){
url=tmp[2];
name=tmp[3];
if(tmp[1].match(/>/)){
i=i+1;
list1.push({name:name,url:url});
}
else
list2.push({name:name,url:url});

if(i==1){
i=-1;
list1=list1.concat(list2.reverse());
list2=[];
}
}
list1;

