var javaImport = new JavaImporter();
javaImport.importPackage(
    Packages.java.lang,
    Packages.javax.crypto.spec,
    Packages.javax.crypto,
    Packages.java.util,
    Packages.java.io,
    Packages.java.security
);
with(javaImport) {
	  var key1 = "UVRcW1RCX19ZQ1RFVkleIikoJSYiIiUk";
	  function newbyte(len) {
	  	  var b=[];
	  	  for(var i=0;i<len;i++){
	  	  	b.push(0x00)
	  	  	}
	  	  return b;
	  	}

    function hexStr(str) {
        if (str != null) {
        	  try{
                if (str.length() >= 2) {
                    var lowerCase = str.toLowerCase();
                    var length = lowerCase.length() / 2;
                    //java.log(length)
                    var bArr = newbyte(length);
                    //java.log(bArr.length)                
                    for (var i = 0; i < length; i++) {
                        var i2 = i * 2;
                        x= lowerCase.substring(i2, i2 + 2);
                        //java.log(x)
                        bArr[i]=Integer.parseInt(x, 16) & 255;
                    }
                    return bArr;
                }
             }catch(e){return null}
        }
        return null;
    };

    function bb(str) {
        var bArr = [];
        if (str == null) {
            str = "";
        }
            var stringBuffer = new StringBuffer(32);
            stringBuffer.append(str);
            while (stringBuffer.length() < 32) {
                stringBuffer.append("0");
            }
            if (stringBuffer.length() > 32) {
                stringBuffer.setLength(32);
            }
            try {
                bArr = stringBuffer.toString().getBytes("UTF-8");
            } catch (e) {
                bArr = null;
            }
            return new SecretKeySpec(bArr, "AES");
    };

    function dd(bArr,str) {
    	      if(bArr==null){return null}
            var b = bb(str);
            var instance = Cipher.getInstance("AES/ECB/PKCS5Padding");
            instance.init(2, b);
            return instance.doFinal(bArr);
    };

   function  decode_1(str) {
        str2 = key1;
        var d = dd(hexStr(str), str2);
            if (d == null) {
                return null;
            }
            return new String(d, "UTF-8");
    }
}

