result = /"text": ".*",/.test(result)?String(result).match(/ "text": "(.*)",/)[1]:"";

if(/extparam=/.test(baseUrl)){
	containerid = baseUrl.match(/containerid=(.*?)&/)[1];
	result = "---复制下方文字，将#超话#粘贴进发现规则---\n"+book.name+`::https://m.weibo.cn/api/container/getIndex?containerid=${containerid}_-_feed&page={\{page}}`
	}

if(/search\?/.test(baseUrl)){
	containerid = baseUrl.replace(/http.*?\?/,'https://m.weibo.cn/api/container/getIndex?');
	result = "---复制下方文字，将#话题#粘贴进发现规则---\n"+book.name+`::${containerid}&page={\{page}}`
	}

if(/uid=/.test(baseUrl)){
	containerid = baseUrl.match(/uid=(\d+)/)[1];
	result = "---复制下方文字，将#用户#粘贴进发现规则---\n"+book.name+`::https://m.weibo.cn/api/container/getIndex?containerid=107603${containerid}_-_WEIBO_SECOND_PROFILE_WEIBO&page_type=03&page={\{page}}`
	}

