ruleToc.chapterList:
id.volumes @tag.li
@js:
    //现实debug(尝试修复正文链接问题，和目录不全)
    //《好友角色的我怎么可能大受欢迎》第三卷12
    //《我的青春恋爱喜剧》
    //2022-8-19
    //原来的代码在源注释（已移除）
    //2022-8-20修复https://w.linovelib.com/novel/2765.html目录加载失败
    //2023-9-30使用易于理解的变量命名
    //2023-10-7处理cid(1)以及连续多个cid(0)
    //2023-11-11解决了一个原来手滑写出的bug
    //2023-11-16处理连续多个卷名

res = result

for (i = 0; i < res.length; i++) {
    java.setContent(res[i])
    if (java.getString("tag.a@href").match(/javascript:cid\(.+\)/gi)) {
        //java.log(java.getString("tag.a@href"))
        if (String(res[(i == res.length - 1?i:i+1)]).match(/javascript:cid\(.+\)/gi)) {
            java.setContent(res[i - 1])
            prevLink = java.getString("tag.a@href")
            if (prevLink == "") {
                java.setContent(res[i - 2])
                prevLink = java.getString("tag.a@href")
            }
            content = java.ajax("https://w.linovelib.com" + prevLink)
            java.setContent(content)
            foot = java.getElements("id.footlink")
            path = prevLink

            //java.log(foot)
            while (String(foot).match("下一页")) {
            	    //java.log(foot)
                str = content.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
                path = String(str).match(/url_next\:'.*?html/)[0].replace("url_next:'", "")
                content = java.ajax("https://w.linovelib.com" + path)
                java.setContent(content)
                foot = java.getElements("id.footlink")
            }

            next = java.ajax("https://w.linovelib.com" + path)
            str = next.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
            path = String(str).match(/url_next\:'.*?html/)[0].replace("url_next:'", "")
            java.log('l:'+path)
            res[i] = String(res[i]).replace(/javascript:cid\(.+\)/gi, path)
        } else {
            nextLink = ""
            for (var j=1;nextLink=="";j++) {
                java.setContent(res[i + j])
                nextLink = java.getString("tag.a@href")
            }
            next = java.ajax("https://w.linovelib.com" + nextLink)
            str = next.match(/<script type\=\"text\/javascript\">var ReadParams.*/)
            path = String(str).match(/url_previous\:'.*?html/)[0].replace("url_previous:'", "")
            java.log(path)

            res[i] = String(res[i]).replace(/javascript:cid\(.+\)/gi, path)
        }
    }

    //java.log(res[i])
}
res

