<script>
function $(t) {
  return document.querySelector(t);
}
function Count() {
  c = $('.count');
  m = $('.muyu');
  c.textContent = ++c.textContent;
  c.style.transform = "scale(1.15)";
  m.style.transform = "scale(.95)";
  setTimeout (function () {
    c.style.transform = "scale(1)";
    m.style.transform = "scale(1)";
  }, 100);
  t = document.createElement('p');
  t.textContent = "功德 + 1";
  $('.center').appendChild(t);
  setTimeout (function () {
    $('p').remove();
  }, 600);
}
function Knock() {
  x = document.createElement('audio');
  x.src = "data:audio/aac;base64,";
  x.onplaying = Count;
  x.onended = x.remove;
  x.play();
}
function Bgm() {
  x = $('#bgm');
  s = $('[onclick="Bgm();"]');
  x.volume = .2;
  if (x.paused) {
    x.play();
    s.textContent = "暂停沉浸式BGM";
  } else {
    x.pause();
    s.textContent = "继续沉浸式BGM";
  }
}
function Auto() {
  s = $('[onclick="Auto();"]');
  d = $('.sound');
  if (!d) {
    Knock();
    x.setAttribute('class', 'sound');
    $('body').appendChild(x);
    d = $('.sound');
    d.hidden = true;
    d.loop = true;
    auto = 1;
    s.textContent = "手动敲";
  } else {
    d.loop = false;
    auto = null;
    s.textContent = "自动敲";
  }
}
document.onkeyup = (e) => {
  if (e.key === " ") {
    Knock();
  }
};
</script>
</html>
