{{@@$..}}
<meta name="viewport" content="width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=0">
<link rel="stylesheet" href="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.css">
<script src="https://cdn.bootcdn.net/ajax/libs/viewerjs/1.10.1/viewer.min.js"></script>
<script src="http://lib.sinaapp.com/js/jquery/2.0.2/jquery-2.0.2.min.js"></script>
<h3 id="title">{{$..title}}</h3>
<h4 >{{$..plain}}</h4>
<h6 id="name">{{$..uname}}</h6>
<div id="box"></div>
<h6 id="loads"><i>加载中...</i></h6>

<script> 
    srclist = "{{$.item_detail.image_list[*].path##\n##\$}}".split("$");
    page = srclist.length

    box = document.getElementById("box")
    loads = document.getElementById("loads")

    h5 = '<h5 style="border-left:2px solid#000;margin-bottom:0;padding-left:5;">'
    h55 = '/' + page + '</h5>'

    img = '<img onload="box.viewer.update()" src="'

    $(document).ready(function () {
    	   
        //初始加载四张图
        for (i = 0; i < 1; i++) {
            box.innerHTML += h5 + (i + 1) + h55
            box.innerHTML += img + srclist[i] + '">'
        }
        new Viewer(box, { title: false, interval: 3000 })
        i = 1
        io = new IntersectionObserver(x => {
            if (x[0].intersectionRatio <= 0) return
            for (I = 0; I < 4; I++) {
                getImg(i, page, srclist)
                i += 1
            }
        }).observe(loads)

    })

    function getImg(i, page, srclist) {

        if (i <= page && srclist[i] != undefined) {
            box.innerHTML += h5 + (i + 1) + h55
            box.innerHTML += img + srclist[i] + '">'
        } else {
            $('#loads').html("");
        }
    }
</script>

