loginUrl:
let log = (text) => {
	if (text) {
		java.toast(text);
		java.log(text);
	}
}

let _getData = () => {
	try {
		return JSON.parse(source.getVariable());
	} catch (err) {
		return {};
	}
}

let _setData = (obj) => {
	if (typeof obj == "object") {
		source.setVariable(JSON.stringify(obj));
	}
}

let _data = _getData();

let formatUrl = (string, replacement) => {
	let replacementText = '/';
	if (replacement) replacementText += replacement;
	return String(string).replace(/\/?$/, replacementText);
}

let ajax = (requestUrl) => {
	let result = java.ajax(requestUrl);
	// 获取发布页通过<iframe>标签内嵌的发布页网址 https://xx/baidu.js
	let _requestUrl = org.jsoup.Jsoup.parse(result).select("iframe").attr("src");
	if (result == '404') {
		// 发布页可直接访问，获取域名 https://xx/
		result = java.ajax(requestUrl.replace(/(https?:\/\/.*?\/).*/, "$1"));
	} else if (_requestUrl != "") {
		//发布页通过<iframe>标签内嵌网址
		result = java.ajax(_requestUrl);
	}
	return org.jsoup.Jsoup.parse(result).select(".line a");
}

//获取发布页列表
let _getPublishUrl = () => {
	return [source.key, "https://5s6s7s8s.com/", "https://qoqoqo.xyz/", "https://5m6m7m8m.com/"];
}

//获取源网址列表
let _getSourceUrl = (urlList) => {
	let logtext;
	let total = 0;
	let publishList = urlList ? urlList : _getPublishUrl();
	if (publishList.length > 0) {
		let requestUrl = formatUrl(publishList[0], "baidu.js");
		let eleObj = ajax(requestUrl);

		if (String(eleObj)) {
			for (let _key in eleObj) {
				let url = String(eleObj[_key].attr("href"));
				if (url.trim() && _data.sourceUrl.indexOf(url) == -1) {
					_data.sourceUrl.push(url);
					total++;
				}
			}
			java.log(`当前发布页${requestUrl}`);
			logtext = `成功获取到${total}个源网址`;
		} else {
			java.log(`当前发布页 ${requestUrl} 未获取到网址`);
			java.log(`【删除当前发布页并重新获取】`);
			return _getSourceUrl(publishList.slice(1));
		}
	} else {
		logtext = `无可用发布页`;
	}
	log(logtext);
	_setData(_data);
}

// 登录UI获取源网址
let _initSourceUrl = () => {
	_data.sourceUrl = [];
	log(`【获取源网址中...】`);
	_getSourceUrl();
}

// 初始化
let _init = () => {
	let obj1 = {
		url: "", //当前选中源网址
	};
	let obj2 = {
		sourceUrl: [], //源网址列表
		sourceSelect: null, //
		loaded: true
	};
	_data = Object.assign({}, obj1, _data, obj2);
	let separator = "，\n　";
	_initSourceUrl();
	java.log(_data.sourceUrl.join(separator));
	log(`【选择默认源网址】`);
	_selectSourceUrl(0);
}

let _selectSourceUrl = (index) => {
	try {
		if (_data.sourceUrl[index] == undefined) {
			log(`当前选项无可用源网址`);
		} else {
			_data.url = formatUrl(_data.sourceUrl[index]);
			log(`切换源网址为${_data.url}`);
		}
	} catch (e) {
		log(`当前选项无可用源网址`);
	}
	_data.sourceSelect = null;
	_setData(_data);
}

let _select = (index) => {
	if (index == undefined) return;
	let title = `[选项${(index + 1)}]`;
	let logtext = `已选择${title}`;

	if (_data.sourceUrl[index] == undefined) {
		logtext = `${title}不可使用`;
		index = null;
	} else if (formatUrl(_data.sourceUrl[index]) == _data.url) {
		logtext = `${title}源网址已在使用`;
		index = null;
	} else if (_data.sourceSelect == index) {
		logtext = `已取消选择${title}`;
		index = null;
	}

	_data.sourceSelect = index;
	log(logtext);
	_setData(_data);
}

let _cancel = () => {
	log(`已取消选择的选项`);
	_data.sourceSelect = null;
	_setData(_data);
}

let _confirm = () => {
	if (_data.sourceSelect == null || _data.sourceSelect == undefined) {
		return log(`请选择任意选项`);
	} else if (_data.sourceUrl[_data.sourceSelect] == undefined) {
		log(`[选项${_data.sourceSelect + 1}]不可使用`);
	} else if (formatUrl(_data.sourceUrl[_data.sourceSelect]) == _data.url) {
		log(`[选项${_data.sourceSelect + 1}]源网址已在使用`);
	} else {
		return _selectSourceUrl(_data.sourceSelect);
	}
	_data.sourceSelect = null;
	_setData(_data);
}


//获取当前地址
let index = 0;
_getUrl = () => {
	if (index < 3) {
		index++;
		try {
			if (!_data.loaded || !_data.url) {
				//初始化
				_init();
				return _getUrl();
			}
			return _data.url;
		} catch (err) {
			//初始化
			_init();
			return _getUrl();
		}
	} else {
		java.toast("初始化失败");
		return null;
	}
}

