	m=h.match(/dsname.+?>\s*(.+?)\s*</)
	if(m){
		java.longToast(m[1]+'登录成功')
	}else{
		java.longToast('登录失效')
	}
}
function login(register){
	info=source.getLoginInfoMap()
	n=info.用户名
	p=info.密码
	path='username='+n+'&psw='+p
	if(register){
		path='ajax=register&ngmar=regnick&'+path+'&repsw='+p
	}else{
		path='ajax=login&'+path
	}
	res=java.get(source.key+'/index.php?'+path,{})
	if(res.body()=='success'){
		if(register){
			java.longToast('注册成功')
		}
	 cookie=res.cookies()
	 source.putLoginHeader('{"Cookie":"PHPSESSID='+cookie.PHPSESSID+';access='+cookie.access+';lang=zh"}')
	}else{
		if(register){
			java.longToast('用户名"'+n+'"注册失败')
		}
		throw new Error(res.body())
	}
}

