//接口设置，以及链接
var v=source.getVariable(),
c="https://www.chunyushuwu.com",
x="https://www.xiarishuwu.com",
q="https://www.qiufengshuwu.com",
d="https://www.dongxueshuwu.com";
var data={
	1:"1：春雨书屋\n"+c,
	2:"2：夏日书屋\n"+x,
	3:"3：秋风书屋\n"+q,
	4:"4：冬雪书屋\n"+d
	}//接口数据
//判断接口
if(!v||v==""){
	v=3;
	source.setVariable(v);
	java.toast("未设置接口，使用默认秋风。")
	}else(v=v)
//设置保存接口
function set(num){
	var show="设置成功！当前接口：\n接口";
	java.toast(show+data[num]);
	return source.setVariable(num)
	}
//获取显示接口
function get(v){
	var show="当前接口：\n接口"+data[v];
	return java.longToast(show)
	}
	//按接口获取网址
var url;
url=v==1?c:v==2?x:v==3?q:v==4?d:q;
url;

