ruleContent:
@js:
function removeParent(doc,txt,index){
e=doc.select(txt).get(0);
while(e!=null&&e.hasParent()&&index>0){
e=e.parent()
index--
}
if(e!=null){e.remove()}
}
var doc=org.jsoup.Jsoup.parse(result,baseUrl);
