function getInput1(){
text=$("#input").value;
list=text.match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/g);
html=""
if(list.length==0){
html="格式化失败！！！请检查是否为带html标签"
}else{
html=""
}
for(i in list){
a=list[i].match(/<a.*?href="([^"]+)".*?>([\s\S]+?)<\/a>/);
html+=a[2]+"::"+a[1]+"\n"
}
$("#input").value=html
}


function getInput2(){
text=$("#input").value;
if(text.match(/\&\&|::/g)){
text=text.replace(/\&\&/g,'\n');
list=text.match(/.*::.*/g);
html=[];
for(i=0;i<list.length;i++){
l=list[i].match(/(.*)::(.*)/);
title=l[1];url=l[2]
html.push({
title:title,
url:url,
style:{}
})
}
$("#input").value=JSON.stringify(html)
}else{
html="";
json=JSON.parse(text);
for(i in json){
html+=json[i].title+"::"+json[i].url+"\n"
}
$("#input").value=html
}
}
function getInput3(flag){
