*{
	z-index:0;
	margin:0;
	padding:0;
	width:100%;
	height:auto;
}
p{
	text-indent:5px;
}
body>p:first-of-type{
	width:100.05%;
	position:sticky;top:285px;
	height:15px;font-size:0.7rem;
	border-bottom-right-radius:5px;
	border-bottom-left-radius:5px;
	background:#000;color:#fff;
	white-space:nowrap;
	overflow:hidden;
	text-overflow:ellipsis;
	z-index:2;
}
iframe{
	position:sticky;top:0;
	background:#000;color:#fff;
	height:285px;
	z-index:2;
}

details{
	width:90%;
	margin:0 5%;
	padding:10 0;
}
summary{
	width:90%;
	margin:0 5%;
	outline:none;
	text-align:center;
}
summary::-webkit-details-marker,.jishu>div{
	display:none;
}
.jiexi>div,.jiekou>div{
	overflow-x:auto;
	overflow-y:hidden;
	white-space:nowrap;
}
.jishu>div.active{
	display:block;
}

button{
	width:21.65%;
	margin:1.25%;
	padding:5px;
	outline:none;
	border-radius:8px;
	font-size:0.7rem;
	text-overflow:ellipsis;
  overflow:hidden;
	position:sticky;
}
button.active{
	z-index:1;
	color:#166188;
	left:0;right:0;
}
.jishu button.active{
	top:300;bottom:0;
}
small{
	position:absolute;
