@js:
var url=source.key;
var web=java.webView(null,url,null);
var html=web;
var j=org.jsoup.Jsoup.parse(html);
var f=j.select('form');
var p=f.select("input")
var a=f.attr("action")
var list=[];
if(web&&html){
	length=p.length>3?2:p.length;
	for(i=0;i<length;i++){
	name=p[i].attr("name")
	value=p[i].attr("value")
	clas=p[i].attr("class")
	if(value!=""){
	add=`${name}=${value}`
	}else if(clas=="text"){
		add=`${name}=${key}`}
	//java.log(add)
	list.push(add)}
	so=list.join("&")
	a+"?"+so;
	}else{java.toast("搜索链接获取失败！")}

ruleContent.content:
@js:
url="https:"+result.match(/initTxt\("([^"]+)","[^"]+"\)/)[1];
r=String(java.ajax(url).match(/_txt_call\(([\s\S]+\})\)/)[1]);
var r = eval('(' + r + ')');
				if(r.content!=null){				
      var e = r.replace;
				for (var n in e) {
					var i = new RegExp(e[n], "ig");
					r.content = r.content.replace(i, n)
				}
				result=r.content
					}else{result="章节加载失败，或者内容正在手打中，请稍后访问~"}

ruleToc.chapterList:
@js:
let v = [],
		list = [];
JSON.parse(result).list.map($=>{

//分卷判定
		V = $.name;
		if(v[v.length-1]!=V&&!/^\s*$/.test(V)){
				v.push(V)
				list.push({
						name: '📖['+V+']📖',
