}

function formatDate(timeStamp){
	let diff = (Date.now() - timeStamp * 1000) / 1000
    if (diff < 60) {
      return 'ÂàöÂàö'
    } else if (diff < 3600) {
      return `${parseInt(diff / 60)}ÂàÜÈíüÂâç`
    } else if (diff < 86400) {
      return `${parseInt(diff / 3600)}Â∞èÊó∂Ââç`
    } else if (diff < 604800) {
      return `${parseInt(diff / 86400)}Â§©Ââç`
    } else if (diff < 2592000) {
      return `${parseInt(diff / 604800)}Âë®Ââç`
    } else if (diff < 31104000) {
      return `${parseInt(diff / 2592000)}‰∏™ÊúàÂâç`
    } else {
      let date = new Date(timeStamp * 1000)
      return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())}`
    }
	}

function createReply(item, addDivider, addPadding) {
    let html = addDivider && `<hr class='hr'>` || ''
    if (addPadding) {
      html += `<div class='box-horizontal padding'>`
      html += `<div class='line-vertical'></div>`
      html += `<div class='box-vertical'>`
    }
    html += `<div class='box-horizontal'>`
    html += `<img class='avatar' src=${item.userAvatar} />`
    html += `<div class='box-vertical'>`
    html += `<div class='font-medium'>${item.username}</div>`
    html += `<div class='font-small color-grey'>${formatDate(item.dateline)}</div>`
    html += `</div>`
    html += `</div>`
    html += `<div class='p'></div>`
    item.message.split(/\r?\n/).map(it => {
      html += `<div class='font-medium'>${it}</div>`
    })
    if (item.picArr && item.picArr.length) {
      html += `<div id='box' class='p'>`
      item.picArr.filter(it => it).map(it => {
        html += `<img onload="box.viewer.update()" class='image' src='${it}' />`
      })
      html += `</div>`
    } else if (item.pic) {
      html += `<div class='p'></div>`
      html += `<a href="${item.pic}"><img class='image' onload="box.viewer.update()" src='${item.pic}' /></a>`
    }
    if (addPadding) {
      html += `</div>`
      html += `</div>`
    }
    return html
  }

