	containerid = baseUrl.replace(/http.*?\?/,'https://m.weibo.cn/api/container/getIndex?');
	result = "---复制下方文字，将#话题#粘贴进发现规则---\n"+book.name+`::${containerid}&page={\{page}}`
	}

