    try {
        for (host of _.url) {
            arr1.push(host);
            arr2.push(host);
            getResult(host, _path.GetHost, 1);
        }
        // 去重
        _.url = arr1.concat(arr3)
            .filter((value, index, self) => {
                return self.indexOf(value) == index && arr2.indexOf(value) == -1;
            });
        _.time = time
        setv(_);
        java.toast("接口校验成功");
    } catch (err) {
        java.toast("未知错误：" + err);
    }
}
// 加载 / 校验
if (_.time < time) {
    verifyUrls();
}
if (_.url[0]) {
    try {
        getResult(_.url[0], uri);
    } catch (err) {
        verifyUrls();
        getResult(_.url[0], uri);
    }
} else {
    java.toast("没有接口");
}

