}

//获取登录信息
if((account&&pwd&&(!wapToken))){
  var loginInfo={
  timeStamp:Date.now(),
  account:account,
  pwd:pwd
}
  var loginInfoUrl=getPostUrl(loginInfo,'login');
  var ddd=String(java.ajax(loginInfoUrl));
  var loginData=JSON.parse(ddd).returnObject;
  userId=loginData.userId;
  siteId=loginData.siteId;
  deskey=loginData.wapToken;
}
java.put('userId',userId);
java.put('deskey',deskey);
java.put('siteId',siteId);
