//警告⚠️勿动
	function atob(input) {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/=';
  let str = String(input).replace(/=+$/, '');
  let output = '';
  if (str.length % 4 === 1) {
    throw new Error('InvalidCharacterError: The string to be decoded is not correctly encoded.');
  }
  for (let bc = 0, bs = 0, buffer, i = 0; buffer = str.charAt(i++); ~buffer && (bs = bc % 4 ? bs * 64 + buffer : buffer, bc++ % 4) ? output += String.fromCharCode(255 & bs >> (-2 * bc & 6)) : 0) {
    buffer = chars.indexOf(buffer);
  }
  return output;
}
 function decode(darr) {
     var arr = [];
     for (var i = 0; i < darr.length; i += 4) {
         arr[(i >> 2)] = ((darr.charCodeAt(i)) | (darr.charCodeAt(i + 1) << 8)) | (darr.charCodeAt(i + 2)) << 16 | (darr.charCodeAt(i + 3)) << 24;
     }
     return arr;
 }
 function encode(darr) {
     var length = darr.length;
     var x = darr[length - 1] & 4294967295;
     var code = '';
     for (var i = 0; i < length; i++) {
         code += String.fromCharCode((darr[i] & 255), (darr[i] >>> 8 & 255), (darr[i] >>> 16 & 255), (darr[i] >>> 24 & 255));
     }
     return code.substring(0, x);
 }
 function jm(con,up) {
     var datas = decode(atob(con));
     var keys = decode(up);
     var a = datas.length  - 1;
     var b = datas[a - 1];
     var c = datas[0];
     var d = 2654435769;
     var e, f;
     var g = Math.floor(6 + 52 / (a + 1));
     var h = (g * d) & 4294967295;
     while (h != 0) {
         f = h >>> 2 & 3;
         for (var i = a; i > 0; i--) {
             b = datas[i - 1];
             e = (((b >>> 5) ^ (c << 2)) + ((c >>> 3) ^ (b << 4))) ^ ((h ^ c) + (keys[(i & 3) ^ f] ^ b));
             c = datas[i] = ((datas[i] - e) & 4294967295);
         }
         b = datas[a];
         e = (((b >>> 5) ^ (c << 2)) + ((c >>> 3) ^ (b << 4))) ^ ((h ^ c) + (keys[(i & 3) ^ f] ^ b));
         c = datas[0] = (datas[0] - e) & 4294967295;
         h = (h - d) & 4294967295;
     }
     jm = atob((encode(datas)));
     return decodeURIComponent(escape(jm));
 }
