    var cX = aQ(c8, false);
    var cT = cS['length'] - 0x1;
    var cP = cS[cT - 0x1],
    cQ = cS[0x0],
    cR = 0x9e3779b9;
    var cL, cM, cN = Math['floor'](0x6+(0x34/(cT+0x1))),
    cO = (cN*cR) & 0xffffffff;

    while (cO!= 0x0) {
        cM = (cO >>> 0x2) & 0x3;
        for (var cW = cT; cW > 0x0; cW--) {
            cP = cS[cW-0x1];
            cL = (((cP>>>0x5)^cQ << 0x2)+((cQ>>>0x3)^(cP<<0x4))) ^ (cO^cQ) + (cX[((cW&0x3)^cM)]^cP);
            cQ = cS[cW] = ((cS[cW] - cL)&0xffffffff);
