ruleExplore.kind:
@js:
k=java.getString('$.view_type');
t=java.getString('$.view_update_time');
if(k&&t!=""){result=k+","+t;}
else{
r=java.getString('$.type');
fl=String(java.get("fl")).split(',');
i=fl.findIndex(i =>i == r);
result=fl[i-1];
}

